import { Component } from '@angular/core';

@Component({
  selector: 'app-store-page',
  templateUrl: './store-page.component.html',
  styleUrls: ['./store-page.component.scss']
})
export class StorePageComponent {
  storeNames = ['Store 1', 'Store 2', 'Store 3', 'Store 4'];
  storeManager = ['Manager 1', 'Manager 2', 'Manager 3', 'Manager 4'];
  storeHours = ['Hours 1', 'Hours 2', 'Hours 3', 'Hours 4'];
  storeAddress = ['Address 1', 'Address 2', 'Address 3', 'Address 4'];
  storePhone = ['Phone 1', 'Phone 2', 'Phone 3', 'Phone 4'];
  afterHours = ['After Hours 1', 'After Hours 2', 'After Hours 3', 'After Hours 4'];
  lunchSchedule = ['Lunch Schedule 1', 'Lunch Schedule 2', 'Lunch Schedule 3', 'Lunch Schedule 4'];
}
